def calculate_sharpe_ratio(returns: pd.Series, risk_free_rate: float = 0.02):
    excess_returns = returns - risk_free_rate / 252
    return np.sqrt(252) * excess_returns.mean() / excess_returns.std()

function RecursiveRuleRenderer({ items }) {
  return (
    <div className="space-y-4">
      {items.map((item) => (
        <div key={item.id} className="relative">
          <RuleBlock item={item} />
          {item.children && (
             // 자식 조건이 있을 경우 자기 자신을 다시 렌더링
             <div className="pl-8 border-l-2">
               <RecursiveRuleRenderer items={item.children} />
             </div>
          )}
        </div>
      ))}
    </div>
  );
}

apiClient.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      // Lock: 첫 번째 401만 갱신 시도
      if (!isRefreshing) {
        isRefreshing = true;
        const newToken = await refreshToken();
        // Queue에 대기 중인 요청들 일괄 재시도
        processQueue(newToken);
      }
      // 현재 요청을 Queue에 추가
      return new Promise((resolve) => {
        failedQueue.push({ resolve, config: originalRequest });
      });
    }
    return Promise.reject(error);
  }
);

// useChartIndicatorManager.ts
const chartRef = useRef<IChartApi | null>(null);
const seriesRef = useRef<ISeriesApi<'Candlestick'> | null>(null);

useEffect(() => {
  if (data && seriesRef.current) {
    // React State 없이 차트 인스턴스 직접 업데이트
    seriesRef.current.setData(data);
  }
}, [data]);

// Cleanup: 메모리 누수 방지
useEffect(() => {
  return () => chartRef.current?.remove();
}, []);

ws.onmessage = (event) => {
  const message = JSON.parse(event.data);
  // API 재호출 없이 캐시 직접 수정
  queryClient.setQueryData(['backtestCore', id], (old) => ({
    ...old,
    progress: message.progress,
    status: message.status,
  }));
  // 완료 시에만 전체 데이터 리페칭
  if (message.status === 'completed') {
    queryClient.invalidateQueries(['backtestCore', id]);
  }
};
